import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import './Home.css';
import {PostData} from '../../services/PostData';

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data:[],
      userFeed: '',
      redirectToReferrer: false,
      name:'',
    };

    this.getUserFeed = this
      .getUserFeed
      .bind(this);
    this.feedUpdate = this.feedUpdate.bind(this);
    this.onChange = this.onChange.bind(this);
    this.deleteFeedAction = this.deleteFeedAction.bind(this);
    this.convertTime = this.convertTime.bind(this);
    this.logout = this.logout.bind(this);
  }

  componentWillMount() {

   if(sessionStorage.getItem("userData")){
    this.getUserFeed();
   }

   else{
    this.setState({redirectToReferrer: false});
   }


  }

  feedUpdate(e) {
    e.preventDefault();
    let data = JSON.parse(sessionStorage.getItem("userData"));
    let postData = { user_id: data.userData.user_id, token: data.userData.token, feed: this.state.userFeed };
    if (this.state.userFeed) {
      PostData('feedUpdate', postData).then((result) => {
        let responseJson = result;
        let K = [responseJson.feedData].concat(this.state.data);
        console.log(K);
       this.setState({data: K , userFeed:''});
      });
    }
  }

  convertTime(created) {
    let date = new Date(created * 1000);
    return date;
  }

  deleteFeedAction(e){
    console.log("HI");
  let updateIndex=e.target.getAttribute('value');
  let feed_id=e.target.getAttribute('data');

  let data = JSON.parse(sessionStorage.getItem("userData"));

  let postData = { user_id: data.userData.user_id, token: data.userData.token, feed_id: feed_id };
  if (postData) {
    PostData('feedDelete', postData).then((result) => {
     this
     .state
     .data.splice(updateIndex,1);
    this.setState({data:this
      .state
      .data});
    });
  }
  }
  getUserFeed() {

    let data = JSON.parse(sessionStorage.getItem("userData"));
    this.setState({name:data.userData.name});
    let postData = { user_id: data.userData.user_id, token: data.userData.token};

    if (data) {
      PostData('feed', postData).then((result) => {
        let responseJson = result;
        this.setState({data: responseJson.feedData});
        console.log(this.state);
      });
    }

  }

  onChange(e){
    this.setState({userFeed:e.target.value});
   }
   logout(){
     sessionStorage.setItem("userData",'');
     sessionStorage.clear();
     this.setState({redirectToReferrer: true});
   }

  render() {
    if (this.state.redirectToReferrer) {
      return (<Redirect to={'/login'}/>)
    }

    return (
      <div className="container">
        <div className="row">
          <div className="col-md-6">
          <h1>Add Product</h1>
          </div>
        </div>
      <div className="row">
        <div className="col-md-6">
        <form role="form">
          <select className="form-control" id="productSelect"><option>Please Select a Product Group</option>
            <option>Bar Soaps</option>
            <option>Lotions</option>
            <option>Creams</option>
          </select>
       <div className="form-group">
         <label for="productname" className="loginFormElement">Username:</label>
         <input className="form-control" id="productname" type="email"/>
       </div>
       <div className="form-group">
         <label for="productprice" className="loginFormElement">Product Price</label>
         <input className="form-control" id="productprice" type="email"/>
       </div>
      <div className="form-group">
      <label className="control-label">Product Image</label>
      <input className="filestyle" data-icon="false" type="file"/>
      </div>
          <div className="form-group">
            <label className="loginformelement" for="productdescription">Product Description</label>
        	  <input id="productdescription" className="form-control input-lg" placeholder="Large" type="text"/><div className="container">
            </div>
          <button type="submit" id="loginSubmit" className="btn btn-success loginFormElement">Add Product</button>
          </div></form>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
